mapper.select:
select save_folder, original_file, save_file
from file_info
where post_id = #{articleNo}

mapper.update:
UPDATE posting
SET title      = #{title},
    content    = #{content},
    sido_code  = #{sidoCode},
    gugun_code = #{gugunCode}
WHERE post_id = #{postId}
  and user_id like #{userId}

mapper.select:
SELECT sido_name
FROM sido
WHERE sido_code = #{sidoCode}

SELECT gugun_code, gugun_name
FROM gugun
WHERE sido_code = #{sidoCode}
